import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Button = ({ onClick, text, className, backgroundColor }) => {
  return (
    <button onClick={onClick} className={className} style={{ backgroundColor }}>
      <span>{text}</span>
    </button>
  )
}

const App = () => {
  const [counter, setCounter] = useState(0);
  const [backgroundColors, setBackgroundColors] = useState({
    plus: '#F5F5F5', // Default color for plus button
    minus: '#F5F5F5', // Default color for minus button
    reset: '#F5F5F5', // Default color for reset button
  });

  const increaseByOne = () => {
    setCounter(counter + 1);
  }

  const decreaseByOne = () => {
    setCounter(counter - 1);
  }

  const handleReset = () => {
    setCounter(0);
  }

  const handleTap = (buttonName) => {
    const color = '#C3E0AC';
    setBackgroundColors({
      ...backgroundColors,
      [buttonName]: color,
    });
  }

  const handleTapEnd = (buttonName) => {
    const color = '#F5F5F5';
    setBackgroundColors({
      ...backgroundColors,
      [buttonName]: color,
    });
  }

  return (
    <div className='counter-container'>
      {/* ... */}
      <Button
        onTouchStart={() => handleTap('plus')}
        onTouchEnd={() => handleTapEnd('plus')}
        onClick={increaseByOne}
        text='+'
        className='plus'
        backgroundColor={backgroundColors.plus}
      />
      <Button
        onTouchStart={() => handleTap('minus')}
        onTouchEnd={() => handleTapEnd('minus')}
        onClick={decreaseByOne}
        text='-'
        className='minus'
        backgroundColor={backgroundColors.minus}
      />
      <Button
        onTouchStart={() => handleTap('reset')}
        onTouchEnd={() => handleTapEnd('reset')}
        onClick={handleReset}
        text='Reset'
        className='reset'
        backgroundColor={backgroundColors.reset}
      />
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
