import React, {useState} from 'react';
import ReactDOM from 'react-dom';


const Button = ({ onTouchStart, onTouchEnd ,onClick, text, className, backgroundColor}) => {
  console.log(backgroundColor)
  return (
    <button onTouchStart={onTouchStart} onTouchEnd={onTouchEnd} onClick={onClick} className={className} style={{backgroundColor}}>
      <span>{text}</span>
    </button>
  )
}
const Display = ({counter}) => { 
  const numbers = counter.toString()

  return (
    <div className='numbers'>
      <div>{numbers[1]}</div>
      <span></span>
      <div>{numbers[0]}</div>
    </div>
  )
}

const App = () => {
  const [counter, setCounter] = useState(0)
  const [backgroundColors, setBackgroundColors] = useState({
    plus: '#F5F5F5', 
    minus: '#F5F5F5',
    reset: '#F5F5F5', 
  });

  const increaseByOne = () => {
    setCounter(counter + 1)
  }
  const decreaseByOne = () => {
    setCounter(counter - 1)
  }
  const handleReset = () => {
    setCounter(0)
  }
  const handleTap = (buttonName) => {
    switch (buttonName) {
      case 'plus': color = '#C3E0AC'
      case 'minus' : color = '#FB8D8D'
      case 'reset' : color = '#B6E9F9'
    }

    setBackgroundColor(color)
  }
  const handleTapEnd = () => {
    const color = '#F5F5F5'
    setBackgroundColor(color)
  }

  return (
    <div className='counter-container'>
      <Display counter = {counter}/>
      <div className='controls'>
        <Button onTouchStart={handleTap(plus)} onTouchEnd={handleTapEnd} onClick={increaseByOne} text='+' className='plus'  backgroundColor={backgroundColor}/>
        <Button onTouchStart={handleTap(minus)} onTouchEnd={handleTapEnd} onClick={decreaseByOne} text='-' className='minus' backgroundColor={backgroundColor}/>
        <Button  onTouchStart={handleTap(reset)} onTouchEnd={handleTapEnd} onClick={handleReset} text='Reset'className='reset' backgroundColor={backgroundColor}/>
      </div>
    </div>
  )
}

ReactDOM.render(<App/>, document.getElementById('root'));
